import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';

function StudentList() {
  const [students, setStudents] = useState([]);
  const [mentors, setMentors] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedMentor, setSelectedMentor] = useState('');

  useEffect(() => {
    fetchStudents();
    fetchMentors();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/students');
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const fetchMentors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/mentors');
      setMentors(response.data);
    } catch (error) {
      console.error('Error fetching mentors:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/students', { name, email });
      fetchStudents();
      setName('');
      setEmail('');
    } catch (error) {
      console.error('Error creating student:', error);
    }
  };

  const handleAssignMentor = async () => {
    try {
      await axios.put(`http://localhost:5000/api/students/${selectedStudent}/assign-mentor`, {
        mentorId: selectedMentor,
      });
      fetchStudents();
      setSelectedStudent('');
      setSelectedMentor('');
    } catch (error) {
      console.error('Error assigning mentor:', error);
    }
  };

  return (
    <div className="student-list-container">
      <h2 className="student-list-title">Student List</h2>
      <form className="student-form" onSubmit={handleSubmit}>
        <input
          className="student-input"
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          className="student-input"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button className="student-submit-btn" type="submit">Add Student</button>
      </form>
      <h3 className="assign-mentor-title">Assign Mentor</h3>
      <select className="student-select" value={selectedStudent} onChange={(e) => setSelectedStudent(e.target.value)}>
        <option value="">Select a student</option>
        {students.map((student) => (
          <option key={student._id} value={student._id}>
            {student.name}
          </option>
        ))}
      </select>
      <select className="mentor-select" value={selectedMentor} onChange={(e) => setSelectedMentor(e.target.value)}>
        <option value="">Select a mentor</option>
        {mentors.map((mentor) => (
          <option key={mentor._id} value={mentor._id}>
            {mentor.name}
          </option>
        ))}
      </select>
      <button className="assign-mentor-btn" onClick={handleAssignMentor} disabled={!selectedStudent || !selectedMentor}>
        Assign Mentor
      </button>
      <ul className="student-list">
        {students.map((student) => (
          <li className="student-item" key={student._id}>
            {student.name} - {student.email} - Mentor: {student.mentor ? student.mentor.name : 'Not assigned'}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StudentList;
