import { toast } from 'react-toastify';

export const showNotification = (message, type = 'info') => {
  toast(message, { type });
};

export const notifyStudentAdded = (student) => {
  showNotification(`Student ${student.name} added successfully`, 'success');
};

export const notifyMentorAdded = (mentor) => {
  showNotification(`Mentor ${mentor.name} added successfully`, 'success');
};

export const notifyMentorAssigned = (student, mentor) => {
  showNotification(`Mentor ${mentor.name} assigned to ${student.name}`, 'success');
};
