import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StudentList() {
  const [students, setStudents] = useState([]);
  const [mentors, setMentors] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedMentor, setSelectedMentor] = useState('');
  const [previousMentor, setPreviousMentor] = useState(null);

  useEffect(() => {
    fetchStudents();
    fetchMentors();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/students');
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const fetchMentors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/mentors');
      setMentors(response.data);
    } catch (error) {
      console.error('Error fetching mentors:', error);
    }
  };

  const handleAssignMentor = async () => {
    try {
      await axios.put(`http://localhost:5000/api/students/${selectedStudent}/assign-mentor`, {
        mentorId: selectedMentor,
      });
      alert('Mentor assigned successfully');
      fetchStudents();
      setSelectedStudent('');
      setSelectedMentor('');
    } catch (error) {
      console.error('Error assigning mentor:', error);
    }
  };

  const handleViewPreviousMentor = async (studentId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/students/${studentId}/previous-mentor`);
      setPreviousMentor(response.data);
    } catch (error) {
      console.error('Error fetching previous mentor:', error);
      setPreviousMentor(null);
    }
  };

  return (
    <div>
      <h2>Student List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Current Mentor</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id}>
              <td>{student.name}</td>
              <td>{student.email}</td>
              <td>{student.mentor ? student.mentor.name : 'Not assigned'}</td>
              <td>
                <button onClick={() => handleViewPreviousMentor(student._id)}>
                  View Previous Mentor
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Assign Mentor</h3>
      <select value={selectedStudent} onChange={(e) => setSelectedStudent(e.target.value)}>
        <option value="">Select a student</option>
        {students.map((student) => (
          <option key={student._id} value={student._id}>
            {student.name}
          </option>
        ))}
      </select>
      <select value={selectedMentor} onChange={(e) => setSelectedMentor(e.target.value)}>
        <option value="">Select a mentor</option>
        {mentors.map((mentor) => (
          <option key={mentor._id} value={mentor._id}>
            {mentor.name}
          </option>
        ))}
      </select>
      <button onClick={handleAssignMentor} disabled={!selectedStudent || !selectedMentor}>
        Assign Mentor
      </button>

      {previousMentor && (
        <div>
          <h3>Previous Mentor</h3>
          <p>Name: {previousMentor.name}</p>
          <p>Email: {previousMentor.email}</p>
        </div>
      )}
    </div>
  );
}

export default StudentList;
