import React, { useState, useEffect } from 'react';
import axios from 'axios';

function MentorList() {
  const [mentors, setMentors] = useState([]);
  const [selectedMentor, setSelectedMentor] = useState('');
  const [unassignedStudents, setUnassignedStudents] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);

  useEffect(() => {
    fetchMentors();
    fetchUnassignedStudents();
  }, []);

  const fetchMentors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/mentors');
      setMentors(response.data);
    } catch (error) {
      console.error('Error fetching mentors:', error);
    }
  };

  const fetchUnassignedStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/students/unassigned');
      setUnassignedStudents(response.data);
    } catch (error) {
      console.error('Error fetching unassigned students:', error);
    }
  };

  const handleAssignStudents = async () => {
    try {
      await axios.post(`http://localhost:5000/api/mentors/${selectedMentor}/assign-students`, {
        studentIds: selectedStudents,
      });
      alert('Students assigned successfully');
      setSelectedStudents([]);
      fetchUnassignedStudents();
    } catch (error) {
      console.error('Error assigning students:', error);
    }
  };

  return (
    <div>
      <h2>Mentor List</h2>
      <select onChange={(e) => setSelectedMentor(e.target.value)}>
        <option value="">Select a mentor</option>
        {mentors.map((mentor) => (
          <option key={mentor._id} value={mentor._id}>
            {mentor.name}
          </option>
        ))}
      </select>
      <h3>Unassigned Students</h3>
      <ul>
        {unassignedStudents.map((student) => (
          <li key={student._id}>
            <input
              type="checkbox"
              onChange={(e) => {
                if (e.target.checked) {
                  setSelectedStudents([...selectedStudents, student._id]);
                } else {
                  setSelectedStudents(selectedStudents.filter((id) => id !== student._id));
                }
              }}
            />
            {student.name}
          </li>
        ))}
      </ul>
      <button onClick={handleAssignStudents} disabled={!selectedMentor || selectedStudents.length === 0}>
        Assign Selected Students
      </button>
    </div>
  );
}

export default MentorList;
