// ChangeMentor.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css';

function ChangeMentor({ studentId }) {
  const [student, setStudent] = useState(null);
  const [mentors, setMentors] = useState([]);
  const [selectedMentor, setSelectedMentor] = useState('');

  useEffect(() => {
    fetchStudent();
    fetchMentors();
  }, []);

  const fetchStudent = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/students/${studentId}`);
      setStudent(response.data);
      setSelectedMentor(response.data.mentor?._id || '');
    } catch (error) {
      console.error('Error fetching student:', error);
    }
  };

  const fetchMentors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/mentors');
      setMentors(response.data);
    } catch (error) {
      console.error('Error fetching mentors:', error);
    }
  };

  const handleChangeMentor = async () => {
    try {
      await axios.put(`http://localhost:5000/api/students/${studentId}/change-mentor`, {
        mentorId: selectedMentor,
      });
      fetchStudent(); // Refresh after changing the mentor
    } catch (error) {
      console.error('Error changing mentor:', error);
    }
  };

  return (
    <div className="change-mentor-container">
      <h2>Change Mentor for {student?.name}</h2>
      <select className="mentor-select" value={selectedMentor} onChange={(e) => setSelectedMentor(e.target.value)}>
        <option value="">Select a mentor</option>
        {mentors.map((mentor) => (
          <option key={mentor._id} value={mentor._id}>
            {mentor.name}
          </option>
        ))}
      </select>
      <button className="change-mentor-btn" onClick={handleChangeMentor} disabled={!selectedMentor}>
        Change Mentor
      </button>
    </div>
  );
}

export default ChangeMentor;
