import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AssignMentorForm from './AssignMentorForm';

function AssignMentorList() {
  const [students, setStudents] = useState([]);
  const [mentors, setMentors] = useState([]);

  useEffect(() => {
    fetchStudents();
    fetchMentors();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/students');
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const fetchMentors = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/mentors');
      setMentors(response.data);
    } catch (error) {
      console.error('Error fetching mentors:', error);
    }
  };

  return (
    <div>
      <h2>Assign or Change Mentor for Students</h2>
      {/* Mentor assignment form */}
      <AssignMentorForm students={students} mentors={mentors} fetchStudents={fetchStudents} />

      {/* List of students and their current mentors */}
      <h3>Student List</h3>
      <ul className="student-list">
        {students.map((student) => (
          <li key={student._id} className="student-item">
            {student.name} - {student.email} - Mentor: {student.mentor ? student.mentor.name : 'Not assigned'}
            {/* Option to change mentor */}
            <AssignMentorForm students={[student]} mentors={mentors} fetchStudents={fetchStudents} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AssignMentorList;
