import React, { useState } from 'react';
import axios from 'axios';

function AssignMentorForm({ students, mentors, fetchStudents }) {
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedMentor, setSelectedMentor] = useState('');

  const handleAssignMentor = async () => {
    try {
      await axios.put(`http://localhost:5000/api/students/${selectedStudent}/assign-mentor`, {
        mentorId: selectedMentor,
      });
      fetchStudents(); // Refresh the student list after assigning/changing a mentor
      setSelectedStudent('');
      setSelectedMentor('');
    } catch (error) {
      console.error('Error assigning/changing mentor:', error);
    }
  };

  return (
    <div>
      <h3 className="assign-mentor-title">Assign or Change Mentor</h3>
      <select
        className="student-select"
        value={selectedStudent}
        onChange={(e) => setSelectedStudent(e.target.value)}
      >
        <option value="">Select a student</option>
        {students.map((student) => (
          <option key={student._id} value={student._id}>
            {student.name}
          </option>
        ))}
      </select>
      <select
        className="mentor-select"
        value={selectedMentor}
        onChange={(e) => setSelectedMentor(e.target.value)}
      >
        <option value="">Select a mentor</option>
        {mentors.map((mentor) => (
          <option key={mentor._id} value={mentor._id}>
            {mentor.name}
          </option>
        ))}
      </select>
      <button
        className="assign-mentor-btn"
        onClick={handleAssignMentor}
        disabled={!selectedStudent || !selectedMentor}
      >
        Assign/Change Mentor
      </button>
    </div>
  );
}

export default AssignMentorForm;
